package com.clonz.blastfromthepast.init;import com.clonz.blastfromthepast.BlastFromThePast;import net.minecraft.Util;import net.minecraft.core.registries.Registries;import net.minecraft.resources.ResourceLocation;import net.minecraft.sounds.SoundEvents;import net.minecraft.world.item.ArmorItem;import net.minecraft.world.item.ArmorMaterial;import net.minecraft.world.item.crafting.Ingredient;import net.neoforged.neoforge.registries.DeferredHolder;import net.neoforged.neoforge.registries.DeferredRegister;import java.util.EnumMap;import java.util.List;public class ModArmorMaterials {    public static final DeferredRegister<ArmorMaterial> ARMOR_MATERIAL = DeferredRegister.create(Registries.ARMOR_MATERIAL, BlastFromThePast.MODID);    public static final DeferredHolder<ArmorMaterial, ArmorMaterial> FROST_BITE = ARMOR_MATERIAL.register("frost_bite", () -> new ArmorMaterial(Util.make(new EnumMap<>(ArmorItem.Type.class), map -> {        map.put(ArmorItem.Type.BOOTS, 1);        map.put(ArmorItem.Type.LEGGINGS, 4);        map.put(ArmorItem.Type.CHESTPLATE, 5);        map.put(ArmorItem.Type.HELMET, 2);        map.put(ArmorItem.Type.BODY, 4);    }), 15, SoundEvents.ARMOR_EQUIP_GENERIC, () -> Ingredient.of(ModItems.SHAGGY_PELT.get()), List.of(new ArmorMaterial.Layer(ResourceLocation.fromNamespaceAndPath(BlastFromThePast.MODID, "frost_bite"))), 0.5F, 0.0F));}